/* import React, { useState, useEffect } from "react"; */
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

import styles from './Article.module.scss'
import { Link, useLocation } from 'react-router-dom';
const Article = ({ getArticleById }) => {
    /*  console.log('getArticleById =>', getArticleById); */
    /*  console.log("Window location: ", window.location.pathname); */
    // const [article, setArticle] = useState('');

    // useEffect(() => {
    //     if (!article) {
    //         getArticle()
    //     }
    // }, []);
    // const getArticle = async () => {
    //     const path = window.location.pathname;
    //     const id = path.split('/').pop();
    //     const myArticle = await getArticleById(id)
    //     setArticle(myArticle);
    //     console.log("article in state: ", myArticle)
    // }

    const article = getArticleById(window.location.pathname.split('/').pop());
    const location = useLocation();

    const { author, img } = location.state;
    const authorToUpperCase = author.split(' ').map(user => user[0].toUpperCase() + user.slice(1)).join(' ');

    return (
        <div className='container'>

            <Card className={styles.card} style={{ width: '100%' }}>
                <Card.Img className={styles.imageStyle} variant="top" src={article.image} />
                <Card.Body>
                    <Card.Title>{article.title}</Card.Title>
                    <div className={styles.containerUserImageAndName}>
                        <Card.Img className={styles.imgUser} variant="top" src={img} />
                        <Card.Text>{authorToUpperCase}</Card.Text>

                    </div>


                    <Card.Text className={styles.textStyle}>
                        {article.text}
                    </Card.Text>


                </Card.Body>
            </Card>

            <div className={styles.buttonDiv}>
                <Link to='/articles'>
                    <Button className={styles.btn} variant="outline-dark" >Back to Articles</Button>
                </Link>

                <Button className={styles.btn} variant="outline-success" type="submit">Save</Button>
            </div>

        </div>
    )
}

export default Article;
