import styles from "./img.module.scss";
import React, { useState, useEffect } from "react";


function useInterval(callback, interval) {
    useEffect(() => {
        const start = new Date().getTime()
        const I = setInterval(() => {
            callback(new Date().getTime() - start)
        }, interval)
        return () => clearInterval(I)
    }, [])
}

function useSlider(N, speed = 5000) {
    const [slider, setSlider] = useState(0)
    useInterval((diff) => {
        setSlider(_=>Math.floor(diff / speed) % N)
    }, 300)
    return slider
}

const imgs = [
    "https://images.unsplash.com/photo-1493770348161-369560ae357d?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    "https://images.unsplash.com/photo-1490645935967-10de6ba17061?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1153&q=80",
    "https://images.unsplash.com/photo-1473093295043-cdd812d0e601?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80",
    "https://images.unsplash.com/photo-1514508985285-52fa488e199a?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80",
    "https://images.unsplash.com/photo-1512668033346-09cfb0d8597b?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1275&q=80",
    "https://images.unsplash.com/photo-1494859802809-d069c3b71a8a?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    "https://images.unsplash.com/photo-1593759608032-9289a5995288?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80",
    "https://images.unsplash.com/photo-1543339308-43e59d6b73a6?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80",
    "https://images.unsplash.com/photo-1543352634-a1c51d9f1fa7?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80",
    "https://images.unsplash.com/photo-1551248429-40975aa4de74?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=990&q=80",

];

const ImgApp = () => {
    const slider = useSlider(imgs.length)
    return (
        <div className="scroller">
            <div
                className="inner"
                style={{
                    width: `${imgs.length * 100}%`,
                    transform: `translateX(-${100*slider/imgs.length}%)`
                }}>
                {imgs.map(src => {
                    return (
                        <img
                            style={{
                                width: `${100 / imgs.length}%`
                            }}
                            key={src}
                            alt=""
                            src={src}
                        />);
                })}
            </div>
        </div>
    )
}


export default ImgApp
