import React, { useState, useEffect } from "react";
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import './Article.scss';
import { Link } from 'react-router-dom';
const Article = () => {


    const data = [
        {
            title: 'keto1',
            text: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Minus hic voluptatibus cumque, nam voluptate enim blanditiis, rerum, eligendi eum voluptatum porro deleniti eveniet dolore? Molestiae obcaecati soluta excepturi provident repellat! Lorem ipsum dolor sit amet consectetur adipisicing elit. Minus hic voluptatibus cumque, nam voluptate enim blanditiis',
            image: 'https://parade.com/wp-content/uploads/2019/12/keto-FTR.jpg'
        }
    ]



    return (
        <div className='container'>

            <Card style={{ width: '100%' }}>
                <Card.Img className='image-style' variant="top" src={data[0].image} />
                <Card.Body>
                    <Card.Title>{data[0].title}</Card.Title>
                    <Card.Text>author</Card.Text>
                    <Card.Text></Card.Text>

                    <Card.Text className='text-style'>
                        {data[0].text}
                    </Card.Text>


                </Card.Body>
            </Card>

            <div className='button-div'>
                <Link to='/articles'>
                    <Button variant="outline-dark" >Back to Articles</Button>
                </Link>
                <Button variant="outline-success" type="submit">Save</Button>
            </div>

        </div>
    )
}

export default Article;
