import React, { Component } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import './CreateArticle.scss';
import { useState } from 'react';
import { Link } from 'react-router-dom';



const CreateArticle = ({ data }) => {

    const [title, setTitle] = useState('title');
    const [text, setText] = useState('text');
    const [file, setFile] = useState(null);
    const [article, setArticle] = useState('');
    const [previewImageSrc, setPreviewImageSrc] = useState("");

    const fileChangeHandler = (event) => {
        let fileReader = new FileReader();
        fileReader.readAsDataURL(event.target.files[0])
        fileReader.onloadend = () => {

            let [file] = event.target.files;
            if (file) {
                setPreviewImageSrc(URL.createObjectURL(file))
            }

            const base64ImageUrl = fileReader.result;
            //console.log('base64Image', base64ImageUrl);

            setFile(base64ImageUrl)
        }
    }

    const handleSubmit = async (event) => {

        event.preventDefault();

        const newArticle = {
            title: title,
            text: text,
            image: file
        }

        const res = await fetch('http://localhost:3369/articles', {
            method: 'POST',
            mode: 'cors',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(newArticle)
        });
        console.log(newArticle)
        setArticle([newArticle])
    }

    return (
        <div className='container'>
            <h2 className='text-center'>Add or Edit an new Article</h2>
            <Row>
                <Col className='g-6'>
                    <Form onSubmit={handleSubmit} >
                        {previewImageSrc ? <img src={previewImageSrc} alt="test" /> : null}
                        <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                            <Form.Label>Title</Form.Label>
                            <Form.Control
                                type="text"
                                placeholder="Enter your Title"
                                value={title}
                                onChange={(e) => setTitle(e.target.value)}

                            />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="Text">

                            <Form.Control as="textarea"
                                rows={3}
                                value={text}
                                onChange={(e) => setText(e.target.value)}

                            />
                        </Form.Group>

                        <Form.Group controlId="formFile" className="mb-3">
                            <div type="button" className=" btn-file btn btn-outline-primary" >
                                Add Photo
                                <Form.Control type="file"
                                    name='file'
                                    onChange={fileChangeHandler}

                                />
                            </div>
                        </Form.Group>

                        <div className='edit-delete-buttons'>
                            <Button variant="outline-success" type="submit">Add</Button>
                            <Link to='articles'>
                                <Button variant="outline-dark">Back to Articles</Button>
                            </Link>
                        </div>


                    </Form>
                </Col>

                <Col className='g-6'>
                    {data.map(article => {
                        return (

                            <Card key={article._id} className='card'>
                                <Card.Img className='image-style' variant="top" src={article.image} />
                                <Card.Body>
                                    <Card.Title>{article.title}</Card.Title>
                                    <Card.Text>author</Card.Text>
                                    <Card.Text></Card.Text>

                                    <Card.Text className='text-style'>
                                        {article.text}
                                    </Card.Text>
                                    <div className='edit-delete-buttons'>
                                        <Button variant="outline-primary">Edit</Button>
                                        <Button variant="outline-danger">Delete</Button>
                                    </div>

                                </Card.Body>
                            </Card>

                        )
                    })}
                </Col>
            </Row>


        </div>
    )
}

export default CreateArticle




