import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import image from './123.jpg';
import './Register.css';
import { FaUser } from "react-icons/fa";

import { MdEmail } from "react-icons/md";
import { BsEye, BsEyeSlash } from "react-icons/bs";

import { React, useState } from 'react';

const Register = () => {


    const [isPasswordShown, setIsPasswordShown] = useState(false);

    const [firstName, setFirstName] = useState('Tareq');
    const [lastName, setlastName] = useState('Mshaal');
    const [email, setEmail] = useState('mashaal@gmail.com');
    const [password, setPassword] = useState('98087jl#hff');
    const [passwordConfirm, setPasswordConfirm] = useState('987654');
    const [file, setFile] = useState(null);
    const [user, setUser] = useState([]);

    const [previewImageSrc, setPreviewImageSrc] = useState("");


    const togglePasswordVisiblity = () => {
        setIsPasswordShown(!isPasswordShown);
    }

    const fileChangeHandler = (event) => {
        let fileReader = new FileReader();
        fileReader.readAsDataURL(event.target.files[0])
        fileReader.onloadend = () => {

            let [file] = event.target.files;
            if (file) {
                setPreviewImageSrc(URL.createObjectURL(file))
            }

            const base64ImageUrl = fileReader.result;


            setFile(base64ImageUrl)
        }
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        const newUser = {
            firstName: firstName,
            lastName: lastName,
            email: email,
            password: password,
            image: file
        }

        const res = await fetch('http://localhost:3369/users', {
            method: 'POST',
            mode: 'cors',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(newUser)
        });
        setUser(newUser)
    }

    return (
        <div className='container '>
            <div className="redister">


                <div className="center p-4 img-cover shadow " style={{ backgroundImage: `url(${image})` }}>


                    <Form Form onSubmit={handleSubmit}>
                        <div className="image-user-div" style={{ backgroundColor: 'white' }}>

                            {previewImageSrc ? <img className='image-user' src={previewImageSrc} alt="test" /> : null}
                        </div>

                        <Form.Group controlId="formFile" className="mb-5">

                            <div className="btn-file btn btn-outline-primary">
                                Add Foto
                                <Form.Control type="file"
                                    onChange={fileChangeHandler}
                                />
                            </div>


                            <h3 className="mt-3 text-center ">Sign Up</h3>
                        </Form.Group>
                        <Row className="mb-1 ">
                            <Form.Group as={Col} controlId="exampleForm.ControlInput1">
                                <Form.Label>Firstname:</Form.Label>
                                <Form.Control
                                    className='border-start-0 border-top-0 border-end-0'
                                    type="text"
                                    placeholder="Enter your Name"
                                    autoComplete="off"
                                    value={firstName}
                                    onChange={(e) => setFirstName(e.target.value)}
                                />
                            </Form.Group>

                            <Form.Group as={Col} controlId="exampleForm.ControlInput1">
                                <Form.Label>Lastname:</Form.Label>
                                <Form.Control
                                    className='border-start-0 border-top-0 border-end-0'
                                    type="text"
                                    placeholder="Enter your Name"
                                    value={lastName}
                                    onChange={(e) => setlastName(e.target.value)}

                                />
                            </Form.Group>
                        </Row>


                        <Form.Label>Email address  </Form.Label>
                        <div className='icons'>
                            <Form.Control type="email"
                                placeholder="Enter email"
                                className='border-start-0 border-top-0 border-end-0'
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$"
                            />
                            <MdEmail className="mail-icon" />
                        </div>





                        <Form.Group className="mb-1" controlId="formBasicPassword">
                            <Form.Label>Password  </Form.Label>
                            <div className='icons'>
                                <Form.Control
                                    placeholder="Password"
                                    className='border-start-0 border-top-0 border-end-0'
                                    type={(isPasswordShown) ? "text" : "password"}
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    minLength='10'
                                />

                                <i
                                    className="password-icon"
                                    onClick={togglePasswordVisiblity}
                                >
                                    {isPasswordShown ? <BsEyeSlash /> : <BsEye />}
                                </i>
                            </div>
                        </Form.Group>


                        <Form.Group className="mb-1" controlId="formBasicPassword">
                            <Form.Label>Confirm Password  </Form.Label>


                            <div className='icons'>
                                <Form.Control
                                    placeholder="Password"
                                    type={(isPasswordShown) ? "text" : "password"}
                                    value={passwordConfirm}
                                    onChange={(e) => setPasswordConfirm(e.target.value)}

                                />



                                <i
                                    className="password-icon"
                                    onClick={() => setIsPasswordShown(!isPasswordShown)}
                                >
                                    {isPasswordShown ? <BsEyeSlash /> : <BsEye />}
                                </i>
                            </div>
                        </Form.Group>
                        <Form.Group className="mb-1" controlId="formBasicCheckbox">

                        </Form.Group>

                        <div className="d-grid gap-2">
                            <Button className="shadow mt-5 btn-md" variant="secondary" type="submit">
                                Sign Up Now
                            </Button>
                        </div>
                    </Form >

                    <>

                    </>
                </div>
            </div >
        </div >

    )

}


export default Register;
