import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import styles from './AllArticles.module.scss';
import { FaThumbsUp } from "react-icons/fa";
import { Link } from 'react-router-dom';
/* import { useState, useEffect } from "react"; */

const AllArticles = ({ data }) => {


    return (

        <Container>
            <h2 className='text-center'>All Articles</h2>


            <Row className="justify-content-md-center">
                <Link to='/create-article'>
                    <Button className={styles.btnAddArticle}
                        variant="outline-success"
                    >
                        Add New Article
                    </Button>
                </Link>

                {data.map(article => {
                    return (
                        <Col md="auto">
                            <Card className={styles.cardArticle}>

                                <Card.Img className={styles.imageStyle} variant="top" src={article.image} />

                                <Card.Body>
                                    <Card.Title>{article.title}</Card.Title>
                                    <Card.Text>author</Card.Text>
                                    <Card.Text></Card.Text>

                                    <Card.Text className={styles.textStyle}>
                                        {article.text}
                                    </Card.Text>

                                    <div className={styles.likeDetails}>
                                        <FaThumbsUp />
                                        <Link to={`/article/${article._id}`}>
                                            <Button variant="outline-success"

                                            >
                                                Details
                                            </Button>
                                        </Link>
                                    </div>

                                </Card.Body>
                            </Card>
                        </Col>
                    )
                })}

            </Row>

        </Container>


    )

}



export default AllArticles;
