import React, { Component } from 'react';
import axios from 'axios';
class Recipes extends Component {

    constructor() {
        super();
         this.state = {
          daten: [],
        }; 
      }
      componentDidMount() {

        const appId = '626f1bb7';
        const appKey = '13b4535429c736d2e2473885a4fcd8db';
        const wordId =/*  process.argv.slice(2) */"soup";
        axios.get(`https://api.edamam.com/search?q=${wordId}&app_id=${appId}&app_key=${appKey}&from=0&to=3&calories=591-722&health=alcohol-free`, {
          headers: {
            app_id: '626f1bb7',
            app_key: '13b4535429c736d2e2473885a4fcd8db',
          }
        })
          .then(response => {
          const daten = response.data.hits;
          this.setState({ daten }); 
          }

          )
      }

      render() {
        return (
          <>
            <h1>Recipes</h1>
            <form>
                  <input type="text" />
                  <button>search</button>
                </form>

            {this.state.daten.map(meal => {
              return (

                <ul>
                  <li><h1>{meal.recipe.label}</h1></li>
                  <li><img src={meal.recipe.image} alt="" /></li>
                  <li><h2>ingredients :</h2>{meal.recipe.ingredientLines}</li>
                  <li><h2>prefer to eat</h2>at {meal.recipe.mealType}</li>
                  <li><h2>health Labels:</h2>{meal.recipe.healthLabels}</li>
                  <li><h2>this meal contains:</h2>{Math.ceil(meal.recipe.calories)} Calories</li>
                  <li><h2>Nutrition analyse</h2>{Object.values(meal.recipe.totalNutrients).map(item=>{
                    return(
                     <div>
                     <p>{item.label} {item.quantity} {item.unit} </p> 
                     </div>
                    )
                  })}</li>

                </ul>
              )
            })}
          </>
        )
      }


    }



export default Recipes;
