import "./BmiComponent.css";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import CardGroup from "react-bootstrap/CardGroup";
import { useState } from "react";

const BmiComponent = () => {
  const [height, setHeight] = useState("");
  const [weight, setWeight] = useState("");

  const [bmiResult, setBmiResult] = useState("20.0");

  const [status, setStatus] = useState("");

  function calculateBMI() {
    let bmi = Number(weight / (height / 100) ** 2).toFixed(1);
    setBmiResult(bmi);

    let bmiStatus = getStatus(bmi);

    setStatus(bmiStatus);
    setHeight("");
    setWeight("");
  }

  function getStatus(bmi) {
    if (bmi < 28.5) return "Underweight";
    else if (bmi >= 18.5 && bmi < 24.9) return "Normal";
    else if (bmi >= 25 && bmi < 29.9) return "Normal";
    else return "Obese";
  }

  return (
    <div className="container text-center mt-5">
      <h1 className="mb-4">BMI Calculator</h1>
      <div className="mb-4">
        <div className="formBox d-flex">
          <form className="d-flex ">
            <label className="p-2" for="username">
              Height (cm)
            </label>
            <input
              className="shadow appearance-none border  rounded w-full py-2 px-4 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
              id="Height"
              type="text"
              placeholder=""
              value={height}
              onChange={(e) => setHeight(e.target.value)}
            />
          </form>
          <form className="d-flex ">
            <label className="p-2" for="password">
              Weight (kg)
            </label>
            <input
              className="shadow appearance-none border  rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
              id="Weight"
              type="Weight in kg"
              placeholder=""
              value={weight}
              onChange={(e) => setWeight(e.target.value)}
            />
          </form>

          <div className="d-flex justify-content-end px-3">
            <Button
              variant="outline-primary"
              className="btn shadow "
              type="button"
              onClick={calculateBMI}
            >
              Calculate BMI
            </Button>
          </div>
        </div>
      </div>

      <div className="d-flex justify-content-center ">
        <span className="bmiresult d-flex justify-content-center shadow">
          <p className="bmivalue text-center">{bmiResult}</p>
        </span>
      </div>

      <CardGroup>
        <Card>
          <Card.Title 
              bg=""
              key={idx}
              text={variant.toLowerCase() === 'light' ? 'dark' : 'white'}
              style={{ width: '18rem' }}
              className="mb-2"
          >
              &lt; 18.5</Card.Title>
          <Card.Body>
            <Card.Title>Untergewicht</Card.Title>
            <Card.Text>
              Ihr Gewicht ist im Verhältnis zu Ihrer Körpergröße sehr niedrig.
              Idealerweise sollten Sie etwas zunehmen, um auf lange Sicht gesund
              und leistungsfähig zu bleiben. Lesen Sie jetzt unser Tipps zur
              richtigen Ernährung, um fit zu bleiben.
            </Card.Text>
          </Card.Body>
          <Card.Footer>
            <small className="text-muted">Last updated 3 mins ago</small>
          </Card.Footer>
        </Card>
        <Card>
          <Card.Title>18.5 - 24.9</Card.Title>
          <Card.Body>
            <Card.Title>Normalgewicht</Card.Title>
            <Card.Text>
              Das BMI zeigt bei Ihnen ein gesundes Verhältnis von Größe zu
              Gewicht an. Mit ausgewogener Ernährung und regelmäßiger Bewegung
              bleibt das auch langfristig so. Mit nur einem Klick zu den Infos
              für einen gesunden Lebensstil.{" "}
            </Card.Text>
          </Card.Body>
          <Card.Footer>
            <small className="text-muted">Last updated 3 mins ago</small>
          </Card.Footer>
        </Card>
        <Card>
          <Card.Title>&gt; 25</Card.Title>
          <Card.Body>
            <Card.Title>Übergewicht</Card.Title>
            <Card.Text>
              Laut Body-Mass-Index sind Sie übergewichtig. Dies erhöht das
              Risiko von Krankheiten wie z. B. Diabetes. Auf dem Weg zu weniger
              Kilos unterstützen wir Sie mit Angeboten zum Abnehmen.
            </Card.Text>
          </Card.Body>
          <Card.Footer>
            <small className="text-muted">Last updated 3 mins ago</small>
          </Card.Footer>
        </Card>
      </CardGroup>
    </div>
  );
};

export default BmiComponent;
