import React, { useState } from 'react';
import SearchIcon from '@mui/icons-material/Search';
import CloseIcon from '@mui/icons-material/Close';
import Card from 'react-bootstrap/Card'
import recipesData from "./recipesData.json";
import Col from 'react-bootstrap/Col'
import Row from 'react-bootstrap/Row'
import "./SearchBar.css"
import ReadMore from "./ReadMore";


function SearchBar({ placeholder, data }) {
    const [filteredData, setFilteredData] = useState([]);
    const [wordEntered, setWordEntered] = useState(""); 


    const handleFilter = (event) => {

        const searchWord = event.target.value;
         setWordEntered(searchWord); 

        const newFilter = data.filter((value) => {

            return value.recipe.label.toLowerCase().includes(searchWord.toLowerCase())
        });
        if (searchWord === "") {
            setFilteredData([])
        } else {
           setFilteredData(newFilter); 
       } 


    };
    const clearInput = () => {
        setWordEntered("")
         setFilteredData([]); 
    }; 


    return (
        <div className="search">
            <div className="searchInputs">
                <input type="text"
                className="SearchRecipe"
                    value={wordEntered} 
                    placeholder={placeholder} onChange={handleFilter} />
                <div className="searchIcon">
                    {filteredData.length === 0 ?   <SearchIcon id="searchIcon" /> :
                        <CloseIcon id="closeIcon"
                            onClick={clearInput} /> */} */}
                </div>
            </div>
          {  filteredData.length !== 0 && (  
                <div className="container">
                    <Row>
                        {filteredData.slice(0, 15).map((value, key) => {
                            return (
                                <Col key={key} className="g-1">
                                    <Card style={{ width: '18rem'}} >
                                        <Card.Img variant="top" src={value.recipe.image} />
                                        <Card.Body>
                                            <Card.Title>{value.recipe.label}</Card.Title>
                                            <Card.Text>
                                                {value.recipe.healthLabels}
                                            </Card.Text>
                                            <ReadMore
                                                key={key} 
                                                data={recipesData} 
                                                label={value.recipe.label}
                                            />
                                        </Card.Body>
                                    </Card>
                                </Col>
                            )
                        })}
                    </Row>
                </div> )} 
        </div>

    )


}

export default SearchBar
