import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Container from 'react-bootstrap/Container';
import image from './123.jpg';
import { MdEmail } from "react-icons/md";
import { BsEye, BsEyeSlash } from "react-icons/bs";
import styles from './LogIn.module.scss';
import { useState } from 'react';
/* import { useSessionStorage } from "react-use-storage"; */



function LogIn(props) {

    const [isPasswordShown, setIsPasswordShown] = useState(false);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const [errorMsg, setErrorMsg] = useState('');

   /*  const [isLogin, setIsLogin, removeIsLogin] = useSessionStorage("islogin", false);
    const [userId, setUserId, removeUserId] = useSessionStorage("userId", '');
 */

    const togglePasswordVisiblity = () => {
        setIsPasswordShown(!isPasswordShown)
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        const user = {
            email: email,
            password: password
        }


        const res = await fetch('http://localhost:3369/users/login', {
            method: 'POST',
            mode: 'cors',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(user)

        });
        console.log('hey user => ', res)
        const jsonResponse = await res.text();
        if (jsonResponse === 'user oder passwort nicht gefunden!') {
            setErrorMsg('Incorrect Email or password');
        }

        if (jsonResponse === 'User eingeloggt') {
            setErrorMsg('Login succeeded');
            /* setIsLogin(true);
            setUserId(res._id) */
            console.log(res._id)
            /* window.location = '/logout'; */
        }



        setEmail('');
        setPassword('');
    }
    return (
        <Container className={styles.divContainer}>
            <div className={styles.signIn}>

                <div className="center p-4 img-cover shadow " style={{ backgroundImage: `url(${image})` }}>


                    <Form onSubmit={handleSubmit}>
                        <Form.Group controlId="formFile" className="mb-5">


                            <h3 className="mt-3 text-center ">Sign In</h3>
                        </Form.Group>


                        <Form.Group className="mb-1" controlId="formBasicEmail">
                            <Form.Label>Email address  </Form.Label>
                            <div className={styles.icons}>
                                <Form.Control type="email"
                                    placeholder="Enter email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}

                                />
                                <MdEmail className={styles.mailIcon} />
                            </div>
                        </Form.Group>


                        <Form.Group className="mb-1" controlId="formBasicPassword">
                            <Form.Label>Password  </Form.Label>
                            <div className={styles.icons}>
                                <Form.Control
                                    placeholder="Password"
                                    type={(isPasswordShown) ? "text" : "password"}
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                />

                                <i
                                    className={styles.passwordIcon}
                                    onClick={togglePasswordVisiblity}
                                >
                                    {isPasswordShown ? <BsEyeSlash /> : <BsEye />}
                                </i>
                            </div>
                        </Form.Group>

                        <p className={styles.errorMsg}>{errorMsg}</p>
                        <div className="d-grid gap-2">
                            <Button className="shadow mt-5 btn-md" variant="secondary" type="submit">
                                Sign In
                            </Button>
                        </div>
                    </Form >


                </div>
            </div>
        </Container >

    )

}


export default LogIn;
