/* import React, { useState, useEffect } from "react"; */
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

import styles from './Article.module.scss'
import { Link, useLocation } from 'react-router-dom';
const Article = () => {



    const location = useLocation();


    const { author, img, article } = location.state;
    const authorToUpperCase = author.split(' ').map(user => user[0].toUpperCase() + user.slice(1)).join(' ');

    return (
        <div className='container'>

            <Card className={styles.card} style={{ width: '100%' }}>
                <Card.Img className={styles.imageStyle} variant="top" src={article.image} />
                <Card.Body>
                    <Card.Title>{article.title}</Card.Title>
                    <div className={styles.containerUserImageAndName}>
                        <Card.Img className={styles.imgUser} variant="top" src={img} />
                        <Card.Text>{authorToUpperCase}</Card.Text>

                    </div>


                    <Card.Text className={styles.textStyle}>
                        {article.text}
                    </Card.Text>


                </Card.Body>
            </Card>

            <div className={styles.buttonDiv}>
                <Link to='/articles'>
                    <Button className={styles.btn} variant="outline-dark" >Back to Articles</Button>
                </Link>

                <Button className={styles.btn} variant="outline-success" type="submit">Save</Button>
            </div>

        </div>
    )
}

export default Article;
