import React, { Component } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import './CreateArticle.scss';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';



const CreateArticle = ({ data, addNewArticle, deleteArticle, updateArticle }) => {

    useEffect(() => {
        console.log('createarticle hat sich verÃ¤ndert', data)


    }, [data])

    const [title, setTitle] = useState('title');
    const [text, setText] = useState('text');
    const [file, setFile] = useState(null);

    const [previewImageSrc, setPreviewImageSrc] = useState("");

    const [idToEdite, setIdToEdite] = useState('');

    const fileChangeHandler = (event) => {
        let fileReader = new FileReader();
        fileReader.readAsDataURL(event.target.files[0])
        fileReader.onloadend = () => {

            let [file] = event.target.files;
            if (file) {
                setPreviewImageSrc(URL.createObjectURL(file))
            }

            const base64ImageUrl = fileReader.result;
            //console.log('base64Image', base64ImageUrl);

            setFile(base64ImageUrl)
        }
    }

    const handleAddSubmit = (event) => {

        event.preventDefault();
        console.log('handle add submit')
        const newArticle = {
            title: title,
            text: text,
            image: file
        }
        addNewArticle(newArticle)



    }
    const handleEditeSubmit = (event) => {
        event.preventDefault();
        console.log('handleEditeSubmit=> ')

        const UpditeArticle = {
            title: title,
            text: text,
            image: file
        }
        updateArticle(idToEdite, UpditeArticle)
        setIdToEdite('')
        console.log(UpditeArticle)

    }

    const updateHandle = (article) => {
        setTitle(article.title);
        setText(article.text);
        setIdToEdite(article._id);

        console.log(article)
    }



    return (
        <div className='container'>
            <h2 className='text-center'>Add or Edit an new Article</h2>
            <Row>
                <Col className='g-6'>
                    <Form onSubmit={idToEdite ? handleEditeSubmit : handleAddSubmit} >
                        {previewImageSrc ? <img className='image-create ' src={previewImageSrc} alt="test" /> : null}
                        <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                            <Form.Label>Title</Form.Label>
                            <Form.Control
                                className='input-text'
                                type="text"
                                placeholder="Enter your Title"
                                value={title}
                                onChange={(e) => setTitle(e.target.value)}

                            />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="Text">

                            <Form.Control as="textarea"
                                rows={3}
                                value={text}
                                onChange={(e) => setText(e.target.value)}

                            />
                        </Form.Group>

                        <Form.Group controlId="formFile" className="mb-3">
                            <div type="button" className=" btn-file btn btn-outline-primary" >
                                Add Photo
                                <Form.Control type="file"
                                    name='file'
                                    onChange={fileChangeHandler}

                                />
                            </div>
                        </Form.Group>

                        <div className='edit-delete-buttons'>
                            <Button variant="outline-success" type="submit">
                                {idToEdite ? 'Edite' : 'Add'}

                            </Button>
                            <Link to='articles'>
                                <Button variant="outline-dark">Back to Articles</Button>
                            </Link>
                        </div>


                    </Form>
                </Col>

                <Col className='g-6'>
                    {data.map(article => {
                        return (

                            <Card key={article._id} className='card'>
                                <Card.Img className='image-style' variant="top" src={article.image} />
                                <Card.Body>
                                    <Card.Title>{article.title}</Card.Title>
                                    <Card.Text>author</Card.Text>
                                    <Card.Text></Card.Text>

                                    <Card.Text className='text-style'>
                                        {article.text}
                                    </Card.Text>
                                    <div className='edit-delete-buttons'>
                                        <Button variant="outline-primary"
                                            onClick={() => updateHandle(article)}
                                        >
                                            Edit
                                        </Button>
                                        <Button variant="outline-danger"
                                            onClick={() => deleteArticle(article._id)}
                                        >
                                            Delete
                                        </Button>
                                    </div>

                                </Card.Body>
                            </Card>

                        )
                    })}
                </Col>
            </Row>


        </div>
    )
}

export default CreateArticle;
