import "./BmiComponent.css";
import Button from "react-bootstrap/Button";
import { useState } from "react";

const BmiComponent = () => {
  const [height, setHeight] = useState("");
  const [weight, setWeight] = useState("");

  const [bmiResult, setBmiResult] = useState(2000);

  const [status, setStatus] = useState("");

  function calculateBMI() {
    let bmi = Number(weight / (height / 100) ** 2).toFixed(1);
    setBmiResult(bmi);

    let bmiStatus = getStatus(bmi);

    setStatus(bmiStatus);
    setHeight("");
    setWeight("");
  }

  function getStatus(bmi) {
    if (bmi < 28.5) return "Underweight";
    else if (bmi >= 18.5 && bmi < 24.9) return "Normal";
    else if (bmi >= 25 && bmi < 29.9) return "Normal";
    else return "Obese";
  }

  return (
    <div className="container text-center mt-5">
      <div className="mb-4">
        <div className="formBox d-flex m-2">
          <form className="d-flex ">
            <label className="p-3 " for="username">
              Height (cm)
            </label>
            <input
              className="shadow appearance-none border  rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
              id="Height"
              type="text"
              placeholder=""
              value={height}
              onChange={(e) => setHeight(e.target.value)}
            />
          </form>
          <form className="d-flex ">
            <label className="p-3" for="password">
              Weight (kg)
            </label>
            <input
              className="shadow appearance-none border  rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
              id="Weight"
              type="Weight in kg"
              placeholder=""
              value={weight}
              onChange={(e) => setWeight(e.target.value)}
            />
          </form>

          <div className="d-flex justify-content-end ">
            <Button
              variant="success"
              className="btn shadow "
              type="button"
              onClick={calculateBMI}
            >
              Calculate BMI
            </Button>
          </div>
        </div>
      </div>

      <div className="d-flex justify-content-center ">
        <span className="bmiresult d-flex justify-content-center shadow">
          <p className="bmivalue text-center">{bmiResult}</p>
        </span>
      </div>
    </div>
  );
};

export default BmiComponent;
