import "./BmiComponent.css";
import Button from "react-bootstrap/Button";
import { useState } from "react";

const BmiComponent = () => {
  const [height, setHeight] = useState("");
  const [weight, setWeight] = useState("");

  const [bmiResult, setBmiResult] = useState(null);

  const [status, setStatus] = useState("");

  function calculateBMI() {
    let bmi = Number(weight / (height / 100) ** 2).toFixed(2);
    setBmiResult(bmi);

    let bmiStatus = getStatus(bmi);

    setStatus(bmiStatus);
    setHeight("");
    setWeight("");
  }

  function getStatus(bmi) {
    if (bmi < 28.5) return "Underweight";
    else if (bmi >= 18.5 && bmi < 24.9) return "Normal";
    else if (bmi >= 25 && bmi < 29.9) return "Normal";
    else return "Obese";
  }

  return (
    <div className="container text-center mt-5">
      <form className="">
        <div className="mb-4">
          <label className="mb-5 p-3 " for="username">
            Height (cm)
          </label>
          <input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            id="Height"
            type="text"
            placeholder=""
            value={height}
            onChange={(e) => setHeight(e.target.value)}
          />
          <div className="w-full max-w-xs">
            <form className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
              <div className="mb-6">
                <label
                  className="block text-gray-700 text-sm font-bold mb-2"
                  for="password"
                >
                  Weight (kg)
                </label>
                <input
                  className="shadow appearance-none border  rounded w-full py-2 px-3 text-gray-700 mb-3 leading-tight focus:outline-none focus:shadow-outline"
                  id="Weight"
                  type="Weight in kg"
                  placeholder=""
                  value={weight}
                  onChange={(e) => setWeight(e.target.value)}
                />
              </div>


              <div className="btn">
                <Button
                  variant="success"
                  className="flex just"
                  type="button"
                  onClick={calculateBMI}
                >
                  Calculate BMI
                </Button>

              </div>

            </form>
          </div>
        </div>
      </form>

                <div className="container bmiresult text-center">
                  <p className="bmivalue text-center">{bmiResult}</p>
                </div>
    </div>
  );
};

export default BmiComponent;
