import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import styles from './CreateArticle.module.scss';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';


import TimeAgo from 'javascript-time-ago';
import ReactTimeAgo from 'react-time-ago';
import de from 'javascript-time-ago/locale/de';




const CreateArticle = ({ data, addNewArticle, deleteArticle, updateArticle }) => {

    TimeAgo.addDefaultLocale(de);


    useEffect(() => {



    }, [data])

    const [title, setTitle] = useState('');
    const [text, setText] = useState('');
    const [file, setFile] = useState(null);
    const [createDate, setCreateDate] = useState(Number);

    const [previewImageSrc, setPreviewImageSrc] = useState("");

    const [idToEdite, setIdToEdite] = useState('');

    const fileChangeHandler = (event) => {
        let fileReader = new FileReader();
        fileReader.readAsDataURL(event.target.files[0])
        fileReader.onloadend = () => {

            let [file] = event.target.files;
            if (file) {
                setPreviewImageSrc(URL.createObjectURL(file))
            }

            const base64ImageUrl = fileReader.result;
            //console.log('base64Image', base64ImageUrl);

            setFile(base64ImageUrl)
        }
    }

    const handleAddSubmit = (event) => {

        event.preventDefault();
        console.log('handle add submit')
        const newArticle = {
            title: title,
            text: text,
            image: file,

        }
        addNewArticle(newArticle)
        setTitle('');
        setText('');
        setPreviewImageSrc('');
    }
    const handleEditeSubmit = (event) => {
        event.preventDefault();
        console.log('handleEditeSubmit=> ')

        const UpditeArticle = {
            title: title,
            text: text,
            image: file
        }
        updateArticle(idToEdite, UpditeArticle)
        setIdToEdite('')
        setTitle('');
        setText('');
        setPreviewImageSrc('');
        console.log(UpditeArticle)

    }

    const updateHandle = (article) => {
        setTitle(article.title);
        setText(article.text);
        setIdToEdite(article._id);
        setCreateDate(article.createDate)
    }



    return (
        <Container className={styles.container}>
            <h2 className='text-center'>Add or Edit an new Article</h2>
            <Row>
                <Col className='g-6'>
                    <Form onSubmit={idToEdite ? handleEditeSubmit : handleAddSubmit} >
                        {previewImageSrc ? <img className={styles.imageCreate} src={previewImageSrc} alt="test" /> : null}
                        <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                            {/* <Form.Label>Title</Form.Label> */}
                            <Form.Control
                                className={styles.inputText}
                                type="text"
                                placeholder="Enter your Title"
                                value={title}
                                onChange={(e) => setTitle(e.target.value)}

                            />
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="Text">

                            <Form.Control as="textarea"
                                className={styles.inputTextArea}
                                rows={3}
                                placeholder="Enter your Text"
                                value={text}
                                onChange={(e) => setText(e.target.value)}

                            />
                        </Form.Group>

                        <Form.Group controlId="formFile" className="mb-3">

                            <Button className={styles.addPhotoBtn}
                                variant="outline-primary"
                            >

                                Add Photo
                                <Form.Control
                                    className={styles.inputFile}
                                    type="file"
                                    name='file'
                                    onChange={fileChangeHandler}

                                />
                            </Button>
                        </Form.Group>

                        <div className={styles.editDeleteButtons}>
                            <Button className={styles.btnWidth} variant="outline-success" type="submit"

                            >
                                {idToEdite ? 'Edite' : 'Add'}



                            </Button>
                            <Link to='articles'>
                                <Button className={styles.btnWidth} variant="outline-dark">Back to Articles</Button>
                            </Link>
                        </div>


                    </Form>
                </Col>

                <Col className='g-6'>
                    {data.map(article => {
                        /*    const username = await article.userId.firstName[0].toUpperCase() + article.userId.firstName.slice(1) + ' ' +
                               article.userId.lastName[0].toUpperCase() + article.userId.lastName.slice(1) */
                        console.log("article in createArticle: ", article)
                        return (

                            <Card key={article._id} className={styles.cardCreateArt}>
                                <Card.Img className={styles.imgArt} variant="top" src={article.image} />
                                <Card.Body>
                                    <Card.Title>{article.title}</Card.Title>
                                    <div className={styles.containerUserImageAndName}>
                                        <Card.Img className={styles.imgUser} variant="top" src={article.userId.image} />
                                        <Card.Text>
                                            {
                                                article.userId.firstName[0].toUpperCase() + article.userId.firstName.slice(1) + ' ' +
                                                article.userId.lastName[0].toUpperCase() + article.userId.lastName.slice(1)
                                            }

                                        </Card.Text>
                                    </div>


                                    <Card.Text>
                                        <ReactTimeAgo date={article.createDate} locale="de-De" />

                                    </Card.Text>

                                    <Card.Text className='text-style'>
                                        {article.text}
                                    </Card.Text>
                                    <div className={styles.editDeleteButtons}>
                                        <Button className={styles.btnWidth} variant="outline-primary"
                                            onClick={() => updateHandle(article)}
                                        >
                                            Edit
                                        </Button>
                                        <Button className={styles.btnWidth} variant="outline-danger"
                                            onClick={() => deleteArticle(article._id)}
                                        >
                                            Delete
                                        </Button>
                                    </div>

                                </Card.Body>
                            </Card>

                        )
                    })}
                </Col>
            </Row>


        </Container>
    )
}

export default CreateArticle;
