import styles from "./imgApp.module.scss";
import React, { useState, useEffect } from "react";
im

function useInterval(callback, interval) {
    useEffect(() => {
        const start = new Date().getTime()
        const I = setInterval(() => {
            callback(new Date().getTime() - start)
        }, interval)
        return () => clearInterval(I)
    }, [])
}

function useSlider(N, speed = 40000) {
    const [slider, setSlider] = useState(0)
    useInterval((diff) => {
        setSlider(_=>Math.floor(diff / speed) % N)
    }, 300)
    return slider
}

const imgs = [
    "https://images.unsplash.com/photo-1494859802809-d069c3b71a8a?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1170&q=80",
    "https://menucalc.com/wp-content/uploads/shutterstock_1748071727-scaled.jpg",
    "http://www.zusammen-gegen-adipositas.de/fileadmin/user_upload/keyvisuals/adipositas-grad-frau.jpg",
    "http://www.zusammen-gegen-adipositas.de/fileadmin/user_upload/erfahrungen/bilder/mann-rot.jpg",
    "https://i1.wp.com/www.medien-akademie.de/wp-content/uploads/2020/06/Online-Seminar-Text-Training.jpg?fit=1200%2C800&ssl=1",

];

const ImgApp = () => {
    const slider = useSlider(imgs.length)
    return (
        <div className={styles.scroller}>
            <div
                className={styles.inner}
                style={{
                    width: `${imgs.length * 100}%`,
                    transform: `translateX(-${100*slider/imgs.length}%)`
                }}>
                {imgs.map(src => {
                    return (<Link to="/recipes">
                        <img
                            className={styles.img}
                            style={{
                                width: `${100 / imgs.length}%`
                            }}
                            key={src}
                            alt=""
                            src={src}
                        />
                        </Link>);
                })}
            </div>
        </div>
    )
}


export default ImgApp
