import React, { useState, useEffect } from "react"
import { useParams } from "react-router-dom";
import axios from "axios";
import Container from "react-bootstrap/esm/Container";

const ReadMore = (props) => {
    const params = useParams()

    const [readMoreData, setReadMoreData] = useState([])

    useEffect(() => {
        console.log("props id", params.id)
        const urlId = params.id.trim();
        //0209cb28fc05320434e2916988f47b71
        //https://api.edamam.com/api/recipes/v2/recipe_0209cb28fc05320434e2916988f47b71?type=public&app_id=ddab3a6f&app_key=60f4a798c728f8f926b6a3563ac5c0ce
        const url = `https://api.edamam.com/api/recipes/v2/recipe_${urlId}?type=public&app_id=ddab3a6f&app_key=60f4a798c728f8f926b6a3563ac5c0ce`
        getReadMoreData(url)
    }, [params])

    const getReadMoreData = (url) => {
        try {
            axios.get(url, {
                app_id: 'ddab3a6f',
                app_key: '60f4a798c728f8f926b6a3563ac5c0ce',


            })
                .then((response) => {
                    const data = response.data;
                    console.log("json data", data.recipe)
                    setReadMoreData(data.recipe)
                })
        }
        catch (error) {
            console.log(error)
            alert(error + 'some error occured ')
        }

    }

    /*  const handleClick=()=>{

         //console.log(props.data.filter(value=> value.recipe.label === props.label).map(item=>item.recipe.shareAs));
         /* const Url = props.data.filter(value=> value.recipe.label === props.label).map(item=>item.recipe.shareAs) */
    /*  window.open(Url.join(), "_self", "resizable,scrollbars,status") */
    // } 



    return (
        <Container>
            <div className="readmore">
                <h1>Tareq</h1>
                {readMoreData !== {} ?
                    <div>
                        <h1>{readMoreData.label}</h1>
                        <img src={readMoreData.image} alt="" />

                        <h2>Ingredients :</h2>
                        {readMoreData.ingredientLines.map(item => <ul><li>{item}</li></ul>)}
                        <h2>Diet friends :</h2>
                        {readMoreData.healthLabels.map(value => <ul><li>{value}</li></ul>)}
                    </div> :
                    <div>
                        Loading ...
                    </div>
                }

            </div>
        </Container>
    )
}
export default ReadMore;
