import React, { useState } from 'react';
/* import SearchIcon from '@mui/icons-material/Search';
import CloseIcon from '@mui/icons-material/Close'; */
import { BsSearch } from 'react-icons/bs';
import {AiOutlineCloseCircle} from 'react-icons/ai';
import Card from 'react-bootstrap/Card'
import recipesData from "./recipesData.json";
import Col from 'react-bootstrap/Col'
import Row from 'react-bootstrap/Row'
import Form from 'react-bootstrap/Form'
import styles from "./SearchBar.module.scss"
 import ReadMore from "./ReadMore"; 
import Button from 'react-bootstrap/Button'
import Container from 'react-bootstrap/Container'
import { Link } from 'react-router-dom';


function SearchBar({ placeholder, data }) {
    const [filteredData, setFilteredData] = useState([]);
    const [wordEntered, setWordEntered] = useState("");


    const handleFilter = (event) => {

        const searchWord = event.target.value;
         setWordEntered(searchWord); 

        const newFilter = data.filter((value) => {

            return value.recipe.label.toLowerCase().includes(searchWord.toLowerCase())
        });

        if (searchWord === "") {
            setFilteredData([])
        } else {
           setFilteredData(newFilter); 
       } 


    };

    const clearInput = () => {
        setWordEntered("")
         setFilteredData([]); 
    }; 


    return (
        <Container>
        <div className={styles.search}>
            <div className={styles.searchInputs}>
            <Form.Control size="lg"
             type="text" 
             className={styles.SearchRecipe}
             value={wordEntered} 
             placeholder={placeholder} onChange={handleFilter} />
               {/*  <input type="text"
                className="SearchRecipe"
                    value={wordEntered} 
                    placeholder={placeholder} onChange={handleFilter} /> */}
                 <div >
                    {filteredData.length === 0 ?   <BsSearch className={styles.searchIcon}id="searchIcon" /> :
                        <AiOutlineCloseCircle className={styles.searchIcon} id="closeIcon"
                            onClick={clearInput} /> }
                </div> 
            </div>
          {  filteredData.length !== 0 && (  
                <div className={styles.container}>
                    <Row>
                        {filteredData.slice(0, 18).map((value, key) => {
                            return (
                                <Col key={key} className={styles.col}>

                                    <Card style={{ width: '18rem'}} className={styles.cardBody}>
                                        <Card.Img variant="top" src={value.recipe.image} className={styles.recipeImg}  />
                                        <Card.Body>
                                            <Card.Title className={styles.cardTitle}>{value.recipe.label}</Card.Title>
                                            <Card.Text className={styles.cardText}>
                                                {value.recipe.healthLabels}
                                            </Card.Text>

                                            <Link 
                                                to={ `/ReadMore/${value.recipe.shareAs.slice(value.recipe.shareAs.indexOf("_")+1)} `} 
                                                state= {{ url : "testus"}}
                                            >
                                            <Button
                                            variant="success"
                                            className={styles.readMore}
                                            /*     key={value.recipe.yield} 
                                                data={recipesData} 
                                                label={value.recipe.label} */
                                            >Read more</Button>
                                            </Link>

                                        </Card.Body>
                                    </Card>
                                </Col>
                            )
                        })}
                    </Row>
                </div> )} 
        </div>
        </Container>
    )


}

export default SearchBar
