import styles from "./BmiComponent.module.scss";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import CardGroup from "react-bootstrap/CardGroup";
import { useState } from "react";

const BmiComponent = () => {
  const [height, setHeight] = useState(0);
  const [weight, setWeight] = useState(0);

  const [bmiResult, setBmiResult] = useState(20.0);

  const [status, setStatus] = useState("");

  function calculateBMI() {
    if(weight !== 0 && height !== 0){

      let bmi = Number(weight / (height / 100) ** 2).toFixed(1);
      setBmiResult(bmi);
      console.log(bmi);
      let bmiStatus = getStatus(bmi);
      setStatus(bmiStatus);
      setHeight(0);
      setWeight(0);
    } 
  }

  function getStatus(bmi) {
    if (bmi < 28.5) return "Underweight";
    else if (bmi >= 18.5 && bmi < 24.9) return "Normal";
    else if (bmi >= 25 && bmi < 29.9) return "Normal";
    else return "Obese";
  }

  return (
    <div className="container bmiApp">
      <div className="formBox mb-4">
      <h1 className="mb-4 text-center">BMI Calculator</h1>

        <div className={styles.formstyle} >
          <form className="form d-flex ">
            <label className="p-2" for="username">
              Height (cm)
            </label>
            <input
              className="mb-2 shadow form-control px-4"
              id="Height"
              type="text"
              placeholder=""
              value={height}
              onChange={(e) => setHeight(e.target.value)}
            />
          </form>
          <form className="form d-flex mx-5">
            <label className="p-2" for="password">
              Weight (kg)
            </label>
            <input
              className="mb-2 shadow form-control px-4"
              id="Weight"
              type="Weight in kg"
              placeholder=""
              value={weight}
              onChange={(e) => setWeight(e.target.value)}
            />
          </form>
        </div>

          <div>
            <Button
              variant="outline-info"
              className="mt-3"
              type="button"
              onClick={calculateBMI}
            >
              Calculate BMI
            </Button>
          </div>
      </div>


      <Card className="bg-dark text-white">
        <Card.Title>{bmiResult}</Card.Title>
      </Card>



        <div className={ styles.dark }>
            <p className={ styles.result }>
                {bmiResult}
            </p>
        </div>






      <CardGroup className="shadow">
        <Card >
          <Card.Title className={styles.title}>
          <span className={styles.titlevalue}> &lt; 18.5</span>
           </Card.Title>
          <Card.Body>
            <Card.Title className="text-center">Untergewicht</Card.Title>
            <Card.Text>
              Ihr Gewicht ist im Verhältnis zu Ihrer Körpergröße sehr niedrig.
              Idealerweise sollten Sie etwas zunehmen, um auf lange Sicht gesund
              und leistungsfähig zu bleiben. Lesen Sie jetzt unser Tipps zur
              richtigen Ernährung, um fit zu bleiben.
            </Card.Text>
          </Card.Body>
        </Card>

        <Card >
          <Card.Title className={styles.title}>
          <span className={styles.titlevalue}>18.5 - 24.9</span>

          </Card.Title>
          <Card.Body>
            <Card.Title className="text-center">Normalgewicht</Card.Title>
            <Card.Text>
              Das BMI zeigt bei Ihnen ein gesundes Verhältnis von Größe zu
              Gewicht an. Mit ausgewogener Ernährung und regelmäßiger Bewegung
              bleibt das auch langfristig so. Mit nur einem Klick zu den Infos
              für einen gesunden Lebensstil.{" "}
            </Card.Text>
          </Card.Body>

        </Card>
        <Card >
          <Card.Title className={styles.title}>
          <span className={styles.titlevalue}>&gt; 25</span>
            </Card.Title>
          <Card.Body>
            <Card.Title className="text-center">Übergewicht</Card.Title>
            <Card.Text >
              Laut Body-Mass-Index sind Sie übergewichtig. Dies erhöht das
              Risiko von Krankheiten wie z. B. Diabetes. Auf dem Weg zu weniger
              Kilos unterstützen wir Sie mit Angeboten zum Abnehmen.
            </Card.Text>
          </Card.Body>

        </Card>
      </CardGroup>
    </div>
  );
};

export default BmiComponent;
