import React, {useState} from "react";
import axios from "axios"
import DonoutDiagram from "../../DonoutDiagram"
import BarChart from "../../BarChart";



const NutritionAnalysis = () => {

    const [wordId, setWordId] = useState("");
    const [data, setData] = useState([]);

    const handleSubmit = async(e)=>{
        e.preventDefault();
        if (!wordId){
            alert("Pleaser enter a Word")
        } else{
            const appKey = '60f4a798c728f8f926b6a3563ac5c0ce';
            const appId = 'ddab3a6f';
            try{
                const data = await axios.get(`https://api.edamam.com/search?q=${wordId}&app_id=${appId}&app_key=${appKey}&from=0&to=3&calories=591-722&health=alcohol-free`)
                setData(data.data)
               console.log(data.data);


            } catch(error){
                console.log(error);
                alert("Some Error occured" +error);
            } 
        }
    }


    return (
        <div classNameName="nutritionanalysis">
            <form onSubmit={handleSubmit}>
                <input 
                    type="text" 
                    onChange={(e)=>setWordId(e.target.value)}
                />
                <input 
                    type="submit" 
                    value="Send"   
                />
            </form>

            {data.length===0?null:<div> 
                {data.hits.map((hit,index)=>(
                    <div key={index}>
                        <h1>{hit.recipe.label}</h1>
                        <p>This meal contains {Math.ceil(hit.recipe.calories)} calories</p>
                        <p>It Prefers to eat at {hit.recipe.mealType}</p>
                        <DonoutDiagram  data={hit.recipe.totalNutrients } />  
                         {/* <BarChart data={hit.recipe.totalNutrients} /> */} 


                    </div>
                ))}




            </div>
            }
        </div>
        )
    }





export default NutritionAnalysis
